import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Person = props => (


        <div className="row">
                <div className="col s12 m7">
                    <div className="card">
                        <div className="card-image">
                        <img src={props.exercise.profile}></img>
                        </div>    
                        <span className="card-title">{props.exercise.first} {props.exercise.last}</span>
                        <a class="btn-floating halfway-fab waves-effect waves-light red"><i class="material-icons">+</i></a>
                        <div className="card-content">
                                <p className="col s12 m7">Username: {props.exercise.username}</p>
                                <p>Description: {props.exercise.description}</p>
                                <p>Duration: {props.exercise.duration}</p>
                                <p>Gender: {props.exercise.gender}</p>
                                <p>Age: {props.exercise.age}</p>
                                <p>City: {props.exercise.city}</p>
                                <p>Region: {props.exercise.stateRegion}</p>
                                <p>Country: {props.exercise.country}</p>
                                <p>Your Name: {props.exercise.yourName}</p>
                                <p>Email: {props.exercise.email}</p>
                                <p>Conditions: {props.exercise.condition}</p>
                                <p>Date: {props.exercise.date}</p>
                        </div>
                        <label>
                            <input 
                            type="submit" 
                            className="btn waves-effect waves-light" 
                            value="Submit"
                            />
                        </label>
                    </div>
                </div>
            </div>
)



const Exercise = props => (
    <tr>
        <td>{props.exercise.username}</td>
        <td>{props.exercise.description}</td>
        <td>{props.exercise.duration}</td>
        <td>{props.exercise.first}</td>
        <td>{props.exercise.last}</td>
        <td>{props.exercise.gender}</td>
        <td>{props.exercise.age}</td>
        <td>{props.exercise.city}</td>
        <td>{props.exercise.stateRegion}</td>
        <td>{props.exercise.country}</td>
        <td>{props.exercise.yourName}</td>
        <td>{props.exercise.email}</td>
        <td>{props.exercise.condition}</td>
        <td>{props.exercise.date}</td>
        <td>{props.exercise.profile}</td>

        <td><Link to={"/edit/"+props.exercise._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
        </td>
    </tr>
)

class ExerciseList extends Component {
    constructor(props) {
        super(props);

        this.deleteExercise = this.deleteExercise.bind(this);

        this.state = {exercises: []};
    }
        componentDidMount() {
            axios.get('http://localhost:5000/exercises/')
            .then(response => {
                this.setState ({ exercises: response.data })
            })
            .catch((error) => {
                console.log(error);
            })
        }

        deleteExercise(id) {
            axios.delete('http://localhost:5000/exercises/' +id)
            .then(res => console.log(res.data));

            this.setState({
                exercises: this.state.exercises.filter(el => el._id !== id)
            })
        }

        exerciseList() {
            return this.state.exercises.map(currentexercise => {
                return <Exercise exercise={currentexercise} deleteExercise={this.deleteExercise} key={currentexercise._id} />;
            })
        }

        personList() {
            return this.state.exercises.map(currentexercise => {
                return <Person exercise={currentexercise} deleteExercise={this.deleteExercise} key={currentexercise._id} />;
            })
        }


    render() {
        return (
        <div>
                <h3>Persons to Heal</h3>
                <table className="table">
                    <thead className="thead-light">
                        <tr>
                            <th>Username</th>
                            <th>Description</th>
                            <th>Duration</th>
                            <th>First</th>
                            <th>Last</th>
                            <th>Gender</th>
                            <th>Age</th>
                            <th>City</th>
                            <th>State / Region</th>
                            <th>Country</th>
                            <th>Your Name</th>
                            <th>Email</th>
                            <th>Condition</th>
                            <th>Date</th>
                            <th>Profile</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.exerciseList() }
                    </tbody>
                </table>
            <div className="container">
                {this.personList()}
            </div>
        </div>    
        )
    };
}

export default ExerciseList;
