import React, { Component } from 'react';
import axios from 'axios';
import TextareaAutosize from 'react-autosize-textarea';


class CreateExercises extends Component {
    constructor(props) {
        super(props);

        this.onChangeUsername= this.onChangeUsername.bind(this);
        this.onChangeDescription= this.onChangeDescription.bind(this);
        this.onChangeDuration= this.onChangeDuration.bind(this);

        this.onSubmit= this.onSubmit.bind(this);



        this.state = {
            username: "",
            description: "",
            duration: 0,

            users: [],

            first: '',
            last: '',
            gender: '',
            age: '',
            city: '',
            stateRegion: '',
            country: '',
            yourName: '',
            email: '',
            condition: '',
            profile: ''
        }
    }

        componentDidMount() {
            axios.get('http://localhost:5000/users/')
            .then(response => {
                if(response.data.length > 0) {
                    this.setState({
                        users: response.data.map(user => user.username), 
                        username: response.data[0].username
                    })
                }
            })
        }

        onChangeUsername = (e) => {
            this.setState({
                username: e.target.value
            });
            }

        onChangeDescription = (e) => {
            this.setState({
                description: e.target.value
            });
            }

        onChangeDuration = (e) => {
            this.setState({
                duration: e.target.value
            });
            }

        onChangeFirst = (e) => {
            this.setState({
                first: e.target.value
            });
            }

        onChangeLast = (e) => {
            this.setState({
                last: e.target.value
            });
            }
        onChangeGender = (e) => {
            this.setState({
                gender: e.target.value
            });
            }
            onChangeAge = (e) => {
                this.setState({
                    age: e.target.value
                });
                }

            onChangeCity = (e) => {
                this.setState({
                    city: e.target.value
                });
                }
            onChangeStateRegion = (e) => {
                this.setState({
                    stateRegion: e.target.value
                });
                }
                onChangeCountry = (e) => {
                    this.setState({
                        country: e.target.value
                    });
                    }

                onChangeYourName = (e) => {
                    this.setState({
                        yourName: e.target.value
                    });
                    }
                onChangeEmail = (e) => {
                    this.setState({
                        email: e.target.value
                    });
                    }
                    onChangeCondition = (e) => {
                        this.setState({
                            condition: e.target.value
                        });
                        }

                    onChangeProfile = (e) => {
                        this.setState({
                            profile: e.target.value
                        });
                        }



        onSubmit = (e) => {


            const exercise = {
                username: this.state.username,
                description: this.state.description,
                duration: this.state.duration,
                first: this.state.first,
                last: this.state.last,
                gender: this.state.gender,
                age: this.state.age,
                city: this.state.city,
                stateRegion: this.state.stateRegion,
                country: this.state.country,
                yourName: this.state.yourName,
                email: this.state.email,
                condition: this.state.condition,
                profile: this.state.profile
            }
            e.preventDefault();

            console.log(exercise);

            axios.post('http://localhost:5000/exercise/add', exercise)
                .then(res => console.log(res.data));


        }

    render() {
        return (
            <div className="container">
            <h3>Healing Request</h3>
            <h4>About the Person in need of healing</h4>
                    <form onSubmit={this.onSubmit}>
                        <div className="form-group">
                            <label>Username: </label>
                                <select ref="userInput"
                                    required
                                    className="form-control"
                                    value={this.state.username}
                                    onChange={this.onChangeUsername}>
                                {
                                    this.state.users.map(function(user){
                                    return <option 
                                    key={user}
                                    value={user}>{user}
                                        </option>;
                                    })
                                }
                                </select>
                        </div>
                        <div className="form-group">
                            <label>Description: </label>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.description.value}
                                    onChange={this.onChangeDescription}
                                    />
                        </div>
                        <div className="form-group">
                            <label>Duration in minutes: </label>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.duration.value}
                                    onChange={this.onChangeDuration}
                                    />
                        </div>
                        <div className="form-group"> 
                            <div>
                                <p>Datepicker</p>
                            </div>
                        </div>
                        <div className="form-group">
            <label>
              First Name
                <input
                  type="text" 
                  className="validate" 
                  value={this.state.first.value} 
                  onChange={this.onChangeFirst}
                  name="first" 
                />
            </label>
        </div>
        <div className="form-group">

            <label>
              Last Name
                <input 
                type="text" 
                className="validate" 
                value={this.state.last.value} 
                onChange={this.onChangeLast} 
                name="last" 
                />
            </label>
        </div>


        <div className="form-group">
            <label>
              Age
                <input 
                type="number" 
                className="validate" 
                value={this.state.age.value} 
                onChange={this.onChangeAge} 
                name="age" 
                />
            </label>
        </div>  
        <div className="form-group">  
            <label>
              Gender
                <input 
                type="text" 
                className="validate" 
                value={this.state.gender.value} 
                onChange={this.onChangeGender}
                name="gender" 
                />
            </label>
        </div>

      <h4>Where They Live</h4>
        <div className="form-group">
            <label>
              City
              <input 
              type="text" 
              value={this.state.city.value} 
              onChange={this.onChangeCity} 
              name="city" 
              />
            </label>
        </div>
        <div className="form-group">
            <label>
              State or Region
              <input 
              type="text" 
              value={this.state.stateRegion.value} 
              onChange={this.onChangeStateRegion} 
              name="state" 
              />
            </label>
        </div>
        <div className="form-group">
            <label>
              Country
              <input 
              type="text" 
              value={this.state.country.value} 
              onChange={this.onChangeCountry} 
              name="country" 
              />
            </label>
        </div>
        <div className="form-group">
            <label>
              Profile Picture
              <input 
              className="btn waves-effect waves-light" 
              type="file" value={this.state.profile.value} 
              onChange={this.onChangeProfile} 
              name="profile"
              />
            </label>
        </div>
    <h4>Conditions requested to heal</h4>
        <div className="form-group">
            <label>
              Conditions
            </label>

            <TextareaAutosize 
            type="text" 
            value={this.state.condition.value} 
            onChange={this.onChangeCondition} 
            name="yourName">
            </TextareaAutosize>
        </div>
    <h4>Contact</h4>
        <div className="form-group">
            <label>
              Your Name
              <input 
              type="text" 
              value={this.state.yourName.value} 
              onChange={this.onChangeYourName} 
              name="yourName" 
              />
            </label>
        </div>
        <div className="form-group">
            <label>
              Email
              <input 
              type="email" 
              value={this.state.email.value} 
              onChange={this.onChangeEmail} 
              name="email" 
              />
            </label>
        </div>          
                        <div>
                            <input 
                                type="submit" 
                                value="Send Healing Request" 
                                className="btn btn-primary"
                                />
                        </div>
                    </form>
            </div>
        )}
    }


export default CreateExercises;
